// Shades.
function clrPreviews(clrName, txtClr){

    // Creating HTML elements.
    var previewAnchor = document.createElement('a');

    // Adding class.
    previewAnchor.className = "transparentContent p_3 curGrabbing mt_20px  txtDecorNone clr"+txtClr+" bg"+clrName+" w_30 h_30 txtCenter borRad_100 fSize_tq";

    //Adding content.
    previewAnchor.innerText = clrName;

    //Adding link.
    previewAnchor.setAttribute("href", "#"+clrName);

    return previewAnchor;
}

